renderpass.code:
float noise(float seed)
{
    return fract(sin(19776.15*seed)*1429.14+sin(0.3*iTime));
}

float smootherstep(float start, float end, float u)
{
    float p  = (u-start)/(end-start);
    p =clamp(p, 0., 1.);
    return 0.5*p*(p*(5.-4.*p)+1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( 2.*fragCoord - iResolution.xy) / iResolution.y;
    float r = length(uv);
    float a = atan(uv.y,uv.x);
    vec3 color ;
    //circle
    float f = 1.-smootherstep(.9, 1., r);
    color = mix(color, vec3(1.,.6,0.), f);
    //crown
    f = (1.-smoothstep(1., 1.03, r+0.1*noise(a)))*smoothstep(0.7, 1., r)*step(0.4,noise(a*r));
    color = mix(color, vec3(1.,.4,0.), f);
    //spots 1
    float l =length(uv-0.3);
    f = noise(sin(0.001*l+0.01*a));
	f*= (1.-smoothstep(.9,1.05,length(uv)));
    f = step(0.5,f);
    color = mix(color, vec3(1.,.2,0.), f);
    //spots 2
    l =length(uv+0.4);
    f = noise(sin(0.001*l+0.01*a));
	f*= (1.-smoothstep(0.8,1.3,length(uv)));
    f = step(0.85,f);
    color = mix(color, vec3(1.,.1,0.), f);
    //spots 
    l =length(uv+0.4);
    f = noise(sin(0.021*l+0.01*a));
	f*= (1.-smoothstep(0.,1.2,length(uv-0.3)));
    f = step(0.7,f);
    color = mix(color, vec3(1.,.05,0.), f);

    fragColor = vec4(color,1.);
}
