void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 m = (iMouse.xy-.5*iResolution.xy)/iResolution.y;

    uv = 2. * uv;
	vec2 r = m.xy;

    vec2 pq = vec2(
        uv.x * uv.x - uv.y * uv.y + r.x*sin(iTime),
        uv.x * uv.y + uv.y * uv.x + r.y*cos(iTime*1.1)
    );
    pq = vec2(
        pq.x * uv.x - pq.y * uv.y + r.x*sin(iTime*1.2),
        pq.x * uv.y + pq.y * uv.x + r.y*cos(iTime*1.3)
    );

    // sample texture and output to screen
    vec4 tc = texture(iChannel0, pq+0.5);
    fragColor = vec4(tc.xyz * tc.a + 1.-tc.a, 1.0);
}
