float p0 = 0.0f;
float p1 = 1.0f;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.yy;

    if (uv.x > 1.0f) 
    {
        fragColor = vec4(0.5f, 0.5f, 0.0f, 1.0f);
		return;
    }

	//uv = abs(uv - 0.5f) * 2.0f;
    uv *= 10.0f;

    float ana = iTime * 2.5f;

    //float power = sin(ana) + 1.5;
    float t = uv.x;
    //t = pow(pow(uv.x, power) + pow(uv.y, power), 1.0f / power);

    t = fract(t);

    float value = clamp((t - p0) / (p1 - p0), 0.0f, 1.0f);

    float gradient = value * 3.0f;
    float offset = ana;
    float spacing = 2.0f;

    float dt = ceil(fract((gradient + offset) / (spacing + 1.0f)) * (spacing + 1.0f));

    float result = dt == spacing ? 1.0f : 0.0f;

    fragColor = vec4(vec3(result), 1.0);
}
