void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //Test sur le niveau de rouge
    int t = int(mod(iTime, 3.));
    float texCol = vec4(texture(iChannel0,uv))[t] ;        
    float dFdX = dFdx(texCol);
   	float dFdY = dFdy(texCol);

   	//iChannelResolution[0] : resolution de la texture en entr√©e
    //iResolution : resolution de l'image de sortie
    vec2 res =  iResolution.xy/iChannelResolution[0].xy;
    float intensity = 15.;
    vec2 dF = vec2(dFdX,dFdY) * res* intensity;
    float gradient = length(dF);    

    fragColor = vec4(gradient); 

    //Fabrice Neyret Solution
    /*
    vec2  R = iResolution.xy,
     U = fragCoord/R;
    float v = texture(iChannel0,U).x;
    vec2 dF =   vec2( dFdx(v), dFdy(v) )*15.*R/iChannelResolution[0].xy; 
    fragColor = vec4( length(dF) );
    */
}
