#define ld(c) texture(iChannel0,c/iResolution.xy)


void mainImage( out vec4 O,vec2 uv)
{
    O = ld(uv);
}

#define ld(c) texture(iChannel0,(c)/iResolution.xy)
#define ld2(c) texture(iChannel1,c/iResolution.xy).rg-.5

vec2 hash21(float p)
{
    #define HASHSCALE3 vec3(.1031, .1030, .0973)

	vec3 p3 = fract(vec3(p) * HASHSCALE3);
	p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.xx+p3.yz)*p3.zy);

}

vec2 getDispNoise(vec2 p,float a){
    p+=iResolution.xy*vec2(.3,.2)*a;
    vec2 d1 = ld2(.1*p);
    d1 += ld2(.21*p)*.5;
    d1 += ld2(.41*p)*.25;
    return d1*15.;
}


void mainImage( out vec4 O,vec2 I)
{
    vec2 u = I+I-iResolution.xy;

    float t = iTime*5.;

    u+=getDispNoise(u,floor(t))+smoothstep(60.,30.,floor(t))*(iResolution.xy*(hash21(-floor(t))-.5));

    vec2 dir = normalize(hash21(floor(t))*2.-1.);
    float mgk = dot(dir,u);
    float s = sqrt(fract(t)*2.)*.1*iResolution.x;
    O =(abs(mgk)>.006*iResolution.x)?
        ld(I-sign(mgk)*iTimeDelta*dir*s)
        :(s<=100.)?
            vec4(1)
            :O-O;
}
