void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;

    float d = length(uv);

    float fft1 = texture(iChannel0, vec2(.1, 0.)).x * .8;
    float fft2 = texture(iChannel0, vec2(.4, 0.)).x;

    vec3 col = vec3(smoothstep(.01, .9, d)) + vec3(.1, .3, .8);
    col += d * 1.2;

    col += smoothstep(.5 + cos(iTime + fract(fft2)) * .02 + .04,
                      .25,
                      d - (fft1 * abs(uv.x))) * 1.2 - vec3(.1, d, fft1) - 
        smoothstep(.3  * sin(iTime * .5) * .002 + .003, 
                   .25,
                   d - (fft1 * abs(uv.x * 1.5))) * .8 - vec3(fft2, d, .1);

    col.rb += fft1 * .2 /  d;
    col.gr += fft2 * cos(iTime * .2) * uv;
    col -= abs(uv.y) * 1.5;
    col -= abs(uv.x) * 1.5;

    fragColor = vec4(col,1.0);
}
