#define nCharges 6
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/(sqrt(iResolution.y*iResolution.x));

    float q0 = 1.0;
    float[nCharges] q = float[nCharges](1.,-1. ,0.5 , -0.7,1.5,-1.3);
    vec2[nCharges] x = vec2[nCharges](vec2(0.2,0.5),vec2(0.8,0.5)
                               ,vec2(0.2,0.6), vec2(0.7, 0.2),vec2(0.1,0.2),vec2(0.5,0.6)); 
    x[0] = iMouse.xy/sqrt(iResolution.y*iResolution.x);


    float U = 0.;
    for(int i = 0; i < nCharges; i++){
        U += q[i]*q0/pow((distance(x[i],uv)),1.);
    }
    U *= -1.;

    vec3 col = vec3(-U/100.,0,U/100.);
    //col = vec3(U/2.,U/4.,U/5.);
    if(distance(x[0],uv)<.05 && iMouse.z > 1.){
     	  //col = vec3(0.,1.,0.);
    }
    float k = mod(U,7.);
    float d = 0.1;
    if(-d < k && k < d){
    	col = vec3(0.5,0.5,0.);   
    }
    fragColor = vec4(col,1.0);
}
