// Adapted from https://www.shadertoy.com/view/XlfXWf

#define LIGHT_RANGE 90.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 light = vec2(.2,.2);

    float angle = atan(iMouse.y, iMouse.x);

    light.x = sin(iTime * 1.0);
    light.y = cos(iTime * 1.0);

    light = iMouse.xy / iResolution.xy;
    light -= 0.5;

    float dist = length(light);

    vec3 finalColor = vec3(.8,.8,.8) * pow(max(dot(normalize(light), normalize(uv - 0.5)), 0.0), LIGHT_RANGE);

    finalColor.rgb *= distance(uv, vec2(0.5));

    finalColor.rgb *= dist;

	fragColor = vec4(finalColor.xyz,1.);
}
