//https://iquilezles.org/articles/intersectors


bool inCircle(vec2 uv, float rayon){
    uv = vec2(uv.x-0.5,uv.y-0.5);
    float inAire = uv.x*uv.x + uv.y*uv.y + rayon*rayon;
    if(inAire > -0.03 && inAire < 0.03 ){
        return true;
    }
    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 colorUp = vec3(0.6,0.7,0.9);

    //vec3 colorDown = vec3(0.55,0.18,0.0);
    vec3 colorDown = texture(iChannel0, vec2(uv.x + iTime, uv.y)).xyz * vec3(0.55,0.18,0.0);

    vec3 colorSnake = vec3(0.2,0.4,0.0);
    float rayon = 0.03;

    float hillTestRight = (sin((uv.x+iTime/3.0)*20.0)+5.0) + cos(uv.x+iTime*4.0);
    float hillTestLeft = (8.0+sin(iTime)*2.0)+1.5;
	vec2 uvCircle = vec2(uv.x, uv.y * hillTestLeft - hillTestRight);


    // Output to screen
    if(uv.y * hillTestLeft > hillTestRight){

        if(inCircle(uvCircle, rayon)){
            fragColor = vec4(colorSnake,1.0);
        }else{
        fragColor = vec4(colorUp,1.0);
        }
    }else{
        fragColor = vec4(colorDown,1.0);
    }

}
