//https://www.shadertoy.com/view/XsXXDn
//credits to 'Danilo Guanabara'

#define t iTime
#define r iResolution.xy

float circleFade(float rr,vec2 center,vec2 uv){
    float col=0.;
    float dist=length(uv-center);
    if(dist<rr){
        col=clamp(dist,0.,1.);
    }
    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    	float angle = 0.1*t;
	mat2 rotationMatrix = mat2(cos(angle), -sin(angle),
                               sin(angle),  cos(angle));

    vec2 uv,p=fragCoord.xy/r;
    uv=p;

    float mData = texelFetch( iChannel0, ivec2(6,0), 0 ).x; //音频数据
	vec3 c;
	float l,z=t;//音频数据影响时间
	for(int i=0;i<3;i++) {

		p-=.5;
		p.x*=r.x/r.y;
		z+=.07;
		l=length(p);
		uv+=p/l*(sin(z)+1.-mData*mData*mData)*abs(sin(9.-z*1.));
        uv*=rotationMatrix;
		c[i]=(.03/length(abs(mod(1.9*uv,1.)-.5)));
	}
    vec2 ct = vec2(.5);

    float dark=circleFade(.1,ct,uv);

    vec3 col=mix(c/l,vec3(dark),.1);
    //vec3 col = vec3(dark);
	fragColor=vec4(col,t);
}
