vec3 hsl2Rgb(in vec3 p_Hsl)
{
    vec3 rgb = clamp(abs(mod(p_Hsl.x * 6.0 + vec3(0.0, 4.0, 2.0), 6.0) - 3.0) - 1.0, 0.0, 1.0);
    return p_Hsl.z + p_Hsl.y * (rgb - 0.5) * (1.0 - abs(2.0 * p_Hsl.z - 1.0));
}

vec2 cat2Pol(in float p_Cx, in float p_Cy)
{
    vec2 pol;
    pol.x = sqrt(p_Cx * p_Cx + p_Cy * p_Cy);
    pol.y = atan(p_Cy, p_Cx) / (2.0 * 3.1415926);
    return pol;
}

vec3 hueSat(in vec2 p_Pos, in float p_Radius, in float p_AspectRatio, in float p_Margin, in float p_ShadowEdge)
{
    float cx = p_Pos.x - 0.5;
    float cy = p_Pos.y - 0.5;

    vec2 pol = cat2Pol(cx, cy * p_AspectRatio);

    vec3 hsl;
    hsl.x = pol.y + 0.66667;
    hsl.y = pol.x /(p_Radius - p_Margin);
    if ((p_Pos.x < p_ShadowEdge) || (p_Pos.x > (1.0 - p_ShadowEdge)))
    {
        hsl.z = 0.15;
    }
    else
    {
        hsl.z = 0.25;
    }

    return hsl2Rgb(hsl);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float aspecRatio = iResolution.y / iResolution.x;

	vec2 uv = fragCoord.xy / iResolution.xy;
    float radius = 0.5;
    float margin = 0.2;
    float shadowEdge = (iResolution.x - iResolution.y) / 2.0 / iResolution.x;
    vec3 color = hueSat(uv, radius, aspecRatio, margin, shadowEdge);

	fragColor = vec4(color, 1.0);
}
