void mainImage(out vec4 fragColor, vec2 fragCoord)
{
    fragColor =  vec4(0.);
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 col = vec4(0.);
	float size = 32.;

    float f[32];
        f[0] = 1.;
        f[1] = 0.;
        f[2] = 1.;
        f[3] = 0.;
        f[4] = 0.;
        f[5] = 0.;
        f[6] = 0.;
        f[7] = 0.;
        f[8] = 1.;
        f[9] = 0.;
        f[10] = 0.;
        f[11] = 0.;
        f[12] = 0.;
        f[13] = 0.;
        f[14] = 0.;
        f[15] = 0.;
        f[16] = 1.;
        f[17] = 0.;
        f[18] = 0.;
        f[19] = 0.;
        f[20] = 0.;
        f[21] = 0.;
        f[22] = 0.;
        f[23] = 0.;
        f[24] = 1.;
        f[25] = 0.;
        f[26] = 1.;
        f[27] = 1.;
        f[28] = 0.;
        f[29] = 0.;
        f[30] = 0.;
        f[31] = 1.;

    float row = floor(uv.x*size+1.),
    y = 2.*fract(uv.x*size);

    float slider = fract(iTime/10.)*size;
    float c_v = f[int(slider)];

    float fade = 2.;
    float slider_fader = floor(uv.x*size);
    float t = fract(iTime- slider_fader);
    float g = pow(1.-t, fade);
    g *= c_v;

    if (uv.y < 0.75 && uv.y > 0.25)
    {   
        col += vec4(smoothstep(3.*fwidth(y),0., y));
        col += vec4(step(1.,row-slider)*0.05 - step(1.,row-slider-1.)*0.05);
       	col += vec4(vec3(g)*3., 1);
    }
    fragColor = col;
    fragColor.rgb = pow(fragColor.rgb, vec3(1.0/2.2));

}
