float rand(vec2 uv){
	float r = sin(fract(dot(uv, vec2(782.264234, 84.2384) + sin( uv.y * 942.))) * 111.4235);
    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(0);

    vec2 iPos = floor(uv);


    for(float i =0.; i < 12.; i++){
    	vec2 pos = vec2(sin(sin(iTime * i * 0.2) + i + iTime * 0.5), rand(vec2(0.5 * i, i)) + sin(iTime + i) * 0.5) + vec2(0., 0.5);
        float ball = 1.3 - distance(pos, uv);

        ball = smoothstep(1., .0, ball) * 0.05;

        //ball = floor(ball + 0.2);
        col += vec3(ball * rand(vec2(i * 20., i * 2.)) * 20., 
                                ball * rand(vec2(sin(i) * 1281., i * 2.)), 
                                ball * rand(vec2((i + 0.5) * 100., i * 120.)) * 20.) * 3.;

    }

    fragColor = vec4(col,1.0);
}
