void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(texelFetch( iChannel0, ivec2(fragCoord), 0 ).rgb, 1.0);
}

#define SCALE 2

int pixel(in ivec2 p) 
{
    float tx = 0.3*texelFetch(iChannel0, p, 0).r
            + 0.6*texelFetch(iChannel0, p, 0).g
        	+ 0.11*texelFetch(iChannel0, p, 0).b;
    return int(step(0.3, tx));
}

float rnd(in vec2 p) 
{
    return fract(sin(p.x*17.0*p.y*13.0)*138.5453123);
}

float init( in vec2 p )
{
    return step(0.8, rnd(p));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float o = 0.0;
    float b = 0.0;
    float g = 0.0;
    ivec2 px = ivec2(fragCoord);
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    vec2 mo = iMouse.xy/iResolution.xy;
    mo.x *= iResolution.x/iResolution.y;
    int nb = 0;

    nb += pixel(ivec2(px) + SCALE*ivec2(-1, -1));
    nb += pixel(ivec2(px) + SCALE*ivec2(-1, 0));
    nb += pixel(ivec2(px) + SCALE*ivec2(-1, 1));
    nb += pixel(ivec2(px) + SCALE*ivec2(0, -1));
    nb += pixel(ivec2(px) + SCALE*ivec2(0, 1));
    nb += pixel(ivec2(px) + SCALE*ivec2(1, -1));
    nb += pixel(ivec2(px) + SCALE*ivec2(1, 0));
    nb += pixel(ivec2(px) + SCALE*ivec2(1, 1));

    int sf = pixel(px);
    sf += int(step(0.9, 1.0-length(uv-mo)/0.5));

    if (sf == 1) 
    {
        o = (nb == 2 || nb == 3) ? 1.0 : 0.0;
        b = (nb == 3) ? 1.0 : 0.0;
        g = (nb == 2) ? 0.5: 0.0;
    } else 
    {
        o = (nb == 3) ? 1.0 : 0.0;
        b = (nb == 4) ? 1.0 : 0.0;
        g = (nb > 7) ? 1.0 : 0.0;
    }

    if (iFrame == 0) 
    {
        o = init(fragCoord/iResolution.xy);
        b = init(fragCoord*2.0/iResolution.xy);
        g = init(fragCoord*3.0/iResolution.xy);
    }

    fragColor = vec4(o,g,b,1.0);

}
