#define num_lines 32

const vec3 colors[4] = vec3[4] (
    vec3( 0.1, 0.0, 0.2  ),
    vec3( 0.4, 0.4, 0.8  ),
    vec3( 0.6, 0.6, 0.9 ),
    vec3( 0.0, 0.0, 0.0 )
);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = fragCoord/iResolution.xy - vec2( 0.5, 0.5 );
    float charge = 0.0;
    float fa = iTime*0.01452 + 0.389;
    for( int idx=0; idx<num_lines;idx++ ) 
    {
        vec2 charge_pos  = vec2(sin( float(idx)*11.89+iTime*0.08)*0.417, 
                                sin( float(idx)*1.77)*0.472);
        vec2 charge_dir  = vec2(sin( float(idx-num_lines/2)*fa ), cos( float(idx-num_lines/2)*fa));
        float dd = dot( pos - charge_pos, charge_dir );
        charge = charge + 1.0 / ( dd*dd );

    }    
    vec3 final_color = mix( colors[ 0 ], colors[ 1 ], smoothstep( 200.0,  3000.0, charge) );
    final_color = mix( final_color, colors[ 2 ], smoothstep( 3000.0,  4000.0, charge) );
    final_color = mix( final_color, colors[ 3 ], smoothstep( 8000.0,  9000.0, charge) );
    fragColor = vec4(final_color,1.0);
}
