void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = fragCoord.xy / iResolution.xy;
	vec2 q = p - vec2(0.34,0.7);

    // Time varying pixel color
    vec3 col = mix( vec3(1.0, 0.4, 0.0), vec3(1.0, 0.8, 0.3), sqrt(p.y));

    // Horizontal gradient by multiplying with the x-Component of the Pixels
    // col *= p.x;

    // Vertical gradient
    // col *= p.y;

    // Diagonal gradient
    // col *= p.x + p.y;

    // nach Pixeln definierter Gradient mittig
    // col *= length (p - vec2(0.5,0.5)); 

    // smoothstep function
    // col *= smoothstep (0.1, 0.2, length(q) ); 

    // Gradient in Formel
    //float r = 0.2;
    //col *= smoothstep (r, r+0.01, length(q) ); 

    // Gradient in Formel_2
    //float r = 0.2 + 0.1*cos( atan(q.y,q.x)*10.0);
    //col *= smoothstep (r, r+0.01, length(q) ); 

    // Gradient in Formel_3
    float r = 0.2 + 0.1*cos( atan(q.y,q.x)*10.0 + 20.0 * q.x + 1.0);
    col *= smoothstep (r, r+0.01, length(q) ); 

    r = 0.015;
    r += 0.002*cos(120.0*q.y);
    r += exp(-40.0*p.y);
    col *= 1.0 - (1.0 - smoothstep (r, r+0.002, abs(q.x-0.25*sin(2.0*q.y)) ))*(1.0-smoothstep(0.0,0.1,q.y));

    // Output to screen
    fragColor = vec4(col,1.0);
}
