void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy-0.5;
	uv.x *= iResolution.x/iResolution.y;
    float a = 3.1415/6.;
    float cs = cos(a), sn = sin(a);
    mat2 rot = mat2(cs, -sn, sn, cs); 
    for (float i=0.0; i<3.; i++ )
        uv = abs(uv*rot);  
    for (int i = 0; i <11; i++) {
        float a = atan(uv.y, uv.x)*(0.5 + sin(iTime*0.2)*0.2);
        sn = sin(a);
        cs = cos(a);
        uv = uv * mat2(cs, -sn, sn, cs);
        uv.y = abs(uv.y) + sin(iTime)*0.04 + 0.04;
        uv.x += 0.2+ sin(iTime)*0.02 + 0.02;
    }
    uv /= 1.7;
    fragColor = vec4(
        smoothstep(0.2, 0.0, fract(abs(uv.y-0.02)+iTime)*0.7),
        smoothstep(0.2, 0.0, fract(abs(uv.y-0.04)+iTime)*0.71),
        smoothstep(0.2, 0.0, fract(abs(uv.y-0.06)+iTime)*0.72),
    1.0);
}
