// iq's colouring function
vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime / 4.0;

    vec2 pos = ( fragCoord - .5 * iResolution.x ) / iResolution.y; 
    pos.y += 1.7;
        vec2 c = vec2( -1.12443+cos(t)/29.0, -0.12918 + sin( t ) / 20.0 );
    float idx = 0.0;
    for( idx=0.0; idx<200.0;idx++ ) 
    {
        pos = vec2( abs(pos.x), abs( pos.y ) );
        float m = dot( pos, pos );
        pos = pos / m + c;
        if( length( pos ) > 4.6512 ) {
            break;
        }
    }    
    float ints = idx / 200.0;
   	vec3 col = pal( ints, vec3(0.5,0.5,0.5),vec3(0.5,0.5,.5),vec3(1.0,0.7,0.4),vec3(0.0,0.15,0.20) );
	fragColor = vec4(col,1.0);
}
