float oneHz(){

   return(fract(iTime)*6.29);
}



vec4 tiler(vec2 i){


    float modu = length(i);

    float z = cos(modu *oneHz())*.5 +.5;



    vec3 o = vec3(z); 


    return vec4(o, 1.0);

}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)*20./iResolution.y;

    vec4 col = tiler(uv);

    // Output to screen
    fragColor = col;
}
