#define T(x) texture(iChannel0, fract((x)/iResolution.xy))

void mainImage(out vec4 c, vec2 u)
{   
    vec4 b2 = sign(.5-iMouse.z-10.0)*texture(iChannel1,u/iResolution.xy)*48.;
    vec2 b = vec2(0.0,0.0);//b2.rg;

    vec4 b3 = texture(iChannel2,u/iResolution.xy);
    b3.xy = b3.xy-b3.zw;
    b3.xy *= vec2(2.);
    //b3.xy = floor(b3.xy/vec2(2.))*vec2(2.);

    float ti = iTime*3.0;
    b+=(b2.bb)*b3.xy/iResolution.xy*8.0;//vec2(sin(ti),cos(ti))*.25;
    u+=b;
    //c=1./u.yyyx;
    c=u.yyyx/1e4;///iTime;
    //for(float t=1.4; t<1e2; t+=t)
        //c += (c.gbar-c)/3.+T(u-c.wz*t);
    for(float t=.06; t<4e2; t+=t)
    	c += c.gbar/4.-c*.3+T(u-c.wz*t);

	c = mix(T(u+c.xy), cos(c), .07);
    //if(u.x>iResolution.x/2.0)
    c = mix(c,texture(iChannel0, (u+c.xy) / iResolution.xy),clamp((u.y/iResolution.y)*1.2+.5,0.5,1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 im2 = iMouse.xy;//
    im2 = vec2(sin(iTime*5.),cos(iTime*5.))*iResolution.xy/2.0+iResolution.xy/2.0;
    fragColor = vec4(clamp(1.0-.015*distance(im2.xy,(fragCoord.xy)),0.0,1.0));
	fragColor.rg *= vec2(im2.xy-fragCoord.xy)/iResolution.xy;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 im2 = iMouse.xy;//
    im2 = vec2(sin(iTime*5.),cos(iTime*5.))*iResolution.xy/2.0-iResolution.xy/2.0;

    vec2 pF = texture(iChannel0,fragCoord/iResolution.xy).xy;
    fragColor = vec4(im2.xy-iResolution.xy/2.0,pF.xy);

    //fragColor = mix(fragColor,texture(iChannel0,fragCoord/iResolution.xy),.5);
}
