const float sensitivity = 0.035;
const float SQRT_2 = sqrt(2.0);

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    mat3 samples = mat3(0.0);
    vec2 uv = vec2(0.0);

    // Collect samples from the surrounding texels:
    for (int i=-1; i < 2; i++) {
        for (int j=-1; j < 2; j++) {
            uv = (fragCoord + vec2(i, j)) / iResolution.xy;
            vec3 curTexel = texture(iChannel0, uv).rgb;
            samples[i + 1][j + 1] = (curTexel.r + curTexel.g + curTexel.b) / 3.0;
        }
    }

    // Check whether the gradient over the area around the current texel is continuous:
    // If it is not continuous, theoretically there is some sort of division or edge.
    bool onEdge = false;
    for (int i=0; i < 3; i++) {
        for (int j=0; j < 3; j++) {
            // Compute the difference between the texel the loop is currently on and the current (center) texel:
            float gradient1 = samples[1][1] - samples[i][j];
            // Compute the difference between the current (center) texel and the texel opposite the texel the loop is currently on:
            float gradient2 = samples[2 - i][2 - j] - samples[1][1];
            // If (i, j) is a corner, the differences need to be divided by sqrt(2) to be normalized:
            if (i == j || (2 - i) == j) {
                gradient1 /= SQRT_2;
                gradient2 /= SQRT_2;
            }
            // Check whether both gradients are going in the same direction (whether the overall gradient is continuous):
            if ((gradient1 >= sensitivity && gradient2 >= sensitivity) || (gradient1 < -sensitivity && gradient2 < -sensitivity)) {
                onEdge = true;
                break;
            }
        }
    }

    // Return a color based on whether the current texel is on an edge:
    vec3 color = onEdge ? vec3(1.0, 0.6, 0.0) : vec3(samples[1][1]);
    fragColor = vec4(color, 1.0);
}
