#define PI 3.141592653589793

vec2 convert(vec2 uv) {
    float s = 2.0/PI;
    float r = length(uv);
    float theta = mod(atan(uv.y , uv.x ) + PI/2.0 + PI, 2.0*PI) - PI;
    return vec2(s * theta, -r*3.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = (2.0*fragCoord.xy-iResolution.xy) / min(iResolution.x, iResolution.y);
    vec2 color = convert(uv);
    float m = step(color.x+ ( fract(iTime*.5)*4.-PI*.5 ),.5);

    fragColor = vec4(vec3(m), 1.0);
}
