void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 O, in vec2 U )
{
    vec3 o = vec3(1,0,-1);
    vec2 pw = 1./iResolution.xy;
    vec2 uv = U*pw;
    #define F(v) texture(iChannel0, uv+pw*v)
    vec4 t = F(o.yy);
    vec4 lap = 8.*F(o.yy)-F(o.xx)-F(o.xy)-F(o.xz)-F(o.yx)-F(o.yz)-F(o.zx)-F(o.zy)-F(o.zz);
    float dt = 0.1;
    O = t - dt*lap;
    if (mod(iTime, 1.) < 0.05) O = texture(iChannel1, uv);
}
