float powerLevel(vec2 pos, float serial) {
    return mod(floor(((pos.x + 10.) * pos.y + serial) * (pos.x + 10.) / 100.), 10.) - 5.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord;
    float serial = iTime;
    vec3 col = vec3((powerLevel(uv, serial) + 5.) / 9.);
    fragColor = vec4(col, 1.0);
}
