void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));



    // Output to screen
    fragColor = vec4(col,1.0);

     float balanco2=(sin((10000.0*iTime)+(uv.x*1000.0))/60.0);


    // Output to screen

    if(uv.y>0.7-balanco2*600.0)
        fragColor = vec4(1,0,0,1)*1.0-balanco2*600.0;







    else if(uv.y<0.4+balanco2*600.0)
        fragColor = vec4(0,0,1,1)*1.0-balanco2*400.0;



                else if(uv.y>0.2-balanco2*600.0)
        fragColor = vec4(1,1,0,1)*1.0-balanco2*300.0;
}
