void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float xshift = 0.5, yshift = 0.5;
    float radius = sqrt(pow(uv.x - xshift,2.0) + pow(uv.y- yshift,2.0));
    float sinewave = cos(100.0*radius +10.0*iTime);//sin(100.0*uv.x)+cos(100.0*uv.y);

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    //fragColor = vec4(col,1.0);
    fragColor = vec4(sinewave, 1.0 - sinewave, sinewave, 1.0);
}
