void mainImage( out vec4 fv, in vec2 fp ) {
  ivec2 R = ivec2(iResolution.xy);
  ivec2 fi = ivec2(fp);
  int scale = 3;
  ivec2 ii = (fi/scale)/3;
  int ch = (fi.x/scale)%3;

  vec3 v = vec3(0.);
  ivec2 m = ivec2(iMouse.xy);
  if (m==ivec2(0)) {
    m = (ivec2(6,7)*2+1)*16;
    if (iResolution.y<256.) m -= 128; // preview fix
  }

  int split = min(512,R.x*2/3);
  if (fi.x<split) v = texelFetch(iChannel0, fi, 0).rgb;
  else
    v[ch] = texelFetch(iChannel0,
      ii+m-ivec2(split+R.x,R.y)/(6*scale), 0)[ch]*.99+.01;

  fv = vec4(pow(v,vec3(1./2.2)),1.0);
}

void mainImage( out vec4 fv, in vec2 fp ) {
  vec3 v;

  if (iResolution.y<256.) fp += 128.; // preview fix

  for (int ch=0; ch<3; ch++) {
    vec2 fp = fp;
    if (texelFetch(iChannel1, ivec2(32,2), 0).r<.5)
      fp += vec2(1./3.,0.)*float(ch-1);
    vec2 uv = fp/512.;
    v[ch] = 1.-texture(iChannel0, uv).r;
  }

  fv.rgb = v;
}
