// Some ideas (thanks!) have been borrowed from 
// https://www.shadertoy.com/view/tdsfz2

void mainImage( out vec4 O, in vec2 U ){  
    vec2 u = 2.*(U-iResolution.xy)/iResolution.xy+1., iM; 
    iM = 7E-3*iMouse.xy+ .2*cos(.2*iTime+ .5*cos(iTime));

    for(int i = 0; i++<4 ; )
    u +=1e-2* sin(-5.*u.y/u.x+vec2(.7,iM.y))*pow(length(u),-5.*iM.x);

    O = vec4(u,u.yx+u.xy);                               
}
