#define N 200.
#define B 32.
#define SS 3.

float random (in vec2 st) {
    return fract(sin(dot(st.xy, vec2(12454.1,78345.2))) * 43758.5);
}

vec2 random2(in vec2 st) {
	return vec2(random(st), random(st));    
}

// https://iquilezles.org/articles/palettes
vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ) {
    return a + b*cos(6.28318 * (c*t + d));
}

vec3 ripple(float t) {
    float a = 0.4;
    float b = 0.92;
    vec3 black = vec3(0);
    vec3 white = vec3(1);
    vec3 col = mix(black, white, t/a);
    col = black * smoothstep(0.0, a, t) + white * smoothstep(a, b, t) + black - smoothstep(b, 1.0, t);
	return 1.-col;    
}

vec4 iterate(vec2 p) {

    vec2 z = vec2(0), c = p;
    float i;

    for(i=0.; i < N; i++) {
        z = mat2(z, -z.y, z.x) * z + c;
        if(dot(z, z) > B*B) break;
	}

    return vec4(z, i - log(log(dot(z, z)) / log(B)) / log(2.), i);    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 R = iResolution.xy;

    vec3 col = vec3(0);
    for(float i=0.; i < SS; i++) {
        vec2 uv = 0.00025*(2. * fragCoord + random2(R+i) - R) / R.y - vec2(1.83236, 0);
        vec4 result = iterate(uv);
        float sn = result.z / N;

        if(result.w == N) { 
            col = vec3(0);
        }
        else {
            col += pal(fract(6.0*sn + 0.5 + 0.32), vec3(.5), vec3(0.5), 
            vec3(1.0,1.0,1.0), vec3(.0, .10, .2)) 
                * (0.1+0.9*ripple(fract(200.*sn))) 
                * (0.2+0.8*ripple(fract(40.*sn + 0.5)))
                * (0.6+0.4*ripple(fract(1200.*sn)));
        }
    }

    fragColor = vec4(col / SS, 1.0);
}
