void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = -.9 * (fragCoord - .5* iResolution.xy ) /iResolution.x;    
    float t = iTime * 0.7;
    float r = fract(t*2.0);    
    bool s = fract(t) > 0.5;
    float k = s ? uv.x : uv.y;
    k -= r - 0.5;
    if(k > 0.0){
        discard;
    }
    // k = sin(k*2.0);
    k = fract(k*7.0)*1.2;
    k = ceil(k-0.7);
    fragColor = vec4(k);
}
