void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    float l = 6.*U.x/R.x;

    O = U.y > R.y/2.
     ? texture ( iChannel0, U/256., U.y >  R.y*3./4. ? l : floor(l) ).xxxx
     : texture ( iChannel1, U/1024.,U.y <  R.y/4.    ? l : floor(l) ).xxxx;

    O = iMouse.z > 0.|| ( length(iMouse.xy)<30. && mod(iTime,2.) < 1. )
        ? .6 + .6 * cos( 6.3*(2.*O.x)  + vec4(0,23,21,0)  )
        : pow(O, vec4(1./2.2));
}
