void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord * (2.0 + mod(iTime, 4.0));

    int x = int(uv.x);
    int y = int(uv.y);
    for (int i = 0; i < 60; i++)
    {

     	if(x % 3 == 1 && y % 3 == 1)
        {
            fragColor = vec4(1.0, 1.0, 1.0, 1.0);
            return;
        }
        x = x / 3;
        y = y / 3;

    }
    fragColor = vec4(0.0, 0.0, 0.0, 0.0);
}
