void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy,
         M = iMouse.xy;
  //O = T(U+vec2(0,1));

  // --- vertical scroll at different speed in R,G,B
             //   offset   lod      gain
    O.r = T(U+vec2(0,1  ), 1.).r;
    O.g = T(U+vec2(0,1.5), 2.).g;
    O.b = T(U+vec2(0,2  ), 3.).b;// * (1.+1./R.y); //  or 1 + cst/(R.x*R.y) ? 

  // --- draw
    if (length(M)<30.) M = R* vec2( .6+.4*sin(iTime), .7); 
    float d = smoothstep( 1.5, 0., length(M-U)-R.y/100. );
    O += d * vec4(1,1,1.3,1); // maybe it's my screen but blue was looking too dark
  //O.a = max(O.r,max(O.g,O.b)); O += (1.-O.a) * d;  // true blend
}
