#define THRESHOLD 0.2

#define history_buffer(uv) texture(iChannel0,uv)
#define accumulator(uv) texture(iChannel1,uv)
#define video(uv) texture(iChannel2,uv)

// Number of hues to use (possibly - 1)
#define HUES 200.0

float rand(float n){
    return fract(cos(n) * 43758.5453123);
}

// https://stackoverflow.com/questions/15095909/from-rgb-to-hsv-in-opengl-glsl
vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x) * 10.0;
}

// https://stackoverflow.com/questions/15095909/from-rgb-to-hsv-in-opengl-glsl
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 prev = history_buffer(uv);
    vec4 cur = video(uv);
    vec4 acc = accumulator(uv);


     vec3 hsv = rgb2hsv(texture(iChannel0, uv).rgb);

    hsv.x = rand(floor(hsv.x * HUES));

    // Output to screen
    fragColor = vec4(hsv2rgb(hsv), 1.);

    if (distance(prev.rgb, cur.rgb) > THRESHOLD) {
  		acc.rgb += cur.rgb - prev.rgb;
    }

    fragColor = clamp(acc, 0., 1.);
}
