void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 gv = uv;
    uv.y += iTime*.1;
    vec3 col = vec3(0.);

    float sBars = 100.;

    uv.y = fract(uv.y*sBars);

    uv.y += 1.-smoothstep(0.,1.,uv.y);

    col.r = uv.y;

    col = texture(iChannel0,gv).rgb*uv.y;

    fragColor = vec4(col,1.0);
}
