vec2 center = vec2(.5,.5);
float radius = .1;
float width =.1;
float height = .1;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float aspR = iResolution.y / iResolution.x;

    vec4 col = vec4(1.0,1.0,1.0,1.0);

    uv *= 3.0;
    vec2 uv2 = floor(uv);
    uv = fract(uv);

    uv.y *= aspR;
    center.y *= aspR;

	float ws = length(center.x-uv.x);
	float hs = length(center.y-uv.y);
	float rs = length(center-uv);

	float evnORodd = mod(uv2.x + uv2.y,2.);

	if(evnORodd == 1. && rs < radius)
	col = vec4(1.0,.0,.0,.0);
	else if(ws <= width && hs <= height && evnORodd == 0.)
	col = vec4(1.0,.0,.0,.0);

	// Output to screen
	fragColor = col;
}
