void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/min(iResolution.x, iResolution.y);
	uv *= 5.;
    float f = smoothstep(-.4,.4,sin(atan(uv.y, uv.x)*4.-length(uv)*5. +iTime) + sin(length(uv)+iTime));
    f = 1.- abs(f-1.)*10.0;
    fragColor = vec4(f,f,f,1);
}
