//Anti-aliasing (natural number)
#define AA 2.
//Loop length in seconds.
#define L 32.
//Animation velocity (must be a factor of L)
#define V vec2(8,4)
//Procedural generation "seed".
#define S 0.
//Color count.
#define C 2.5

//Logo parameters.
#define A .200
#define B .140

vec2 hash(vec2 p, float t)
{
    float a = mod(t,L/V.x);
    vec2 m = mod(p,L/V);
    return fract(cos(mat2(31,-41,59,-26)*m+a*56.+S)*535.);
}
vec3 shade(vec2 p)
{
    vec2 m = mod(p,L/V);
    float f = fract(m.x/5.-m.y/5.);
    vec3 c =         vec3( 64, 78,203) / 255.;
    c = (f<.2) ? c : vec3(196, 96,250) / 255.;
    c = (f<.4) ? c : vec3( 33,206,132) / 255.;
    c = (f<.6) ? c : vec3(249, 80,122) / 255.;
    c = (f<.8) ? c : vec3(254, 178,36) / 255.;
    return c;
}
vec2 value(vec2 p)
{
    float t = iTime/V.x;
    float a = fract(t);
 	vec2 f = floor(p);
    vec2 s = p-f;
    s *= s*(3.-2.*s);

    const vec2 o = vec2(0,1);
    return mix(mix(mix(hash(f+o.xx,t-a   ),hash(f+o.yx,t-a   ),s.x),
        	       mix(hash(f+o.xy,t-a   ),hash(f+o.yy,t-a   ),s.x),s.y),
               mix(mix(hash(f+o.xx,t-a+1.),hash(f+o.yx,t-a+1.),s.x),
        	       mix(hash(f+o.xy,t-a+1.),hash(f+o.yy,t-a+1.),s.x),s.y),a)-.5;
}
vec3 stripe(in vec2 p)
{
    #define tau 6.2831
    p = p+value(p)+value(p/.2)*.06+iTime/V;
    vec3 o = vec3(0);

    float d = 4.;
 	vec2 f = floor(p);

    for(float xx = -1.;xx<=1.;xx++)
    for(float yy = -1.;yy<=1.;yy++)
    {
        vec2 v = hash(f+vec2(xx,yy),0.)*1.4-p+f+vec2(xx,yy);
        float l = length(v);
        if (l<=d)
        {
            d = l;
            o = shade(f+vec2(xx,yy));
        }
    }
    return o;
}
vec3 gm(vec2 p)
{
 	vec2 u = (p-iResolution.xy/2.)/iResolution.y;

    float d = abs(abs(u.x)+abs(u.y)-A-B)-B;
    vec2 b = step(abs(u+vec2(-B-A,B)),vec2(B,B));
    d *= 1.-b.x*b.y;
    b = step(abs(u+vec2(-A,A)/2.),vec2(A/2.));
    d -= b.x*b.y;
    return (d>=0.)? vec3(32,34,37) / 255. : stripe(u*C);
}
void mainImage(out vec4 color, in vec2 coord)
{
    vec4 col = vec4(0);

    for(float xx = -.5;xx<=.5;xx+=1./AA)
    for(float yy = -.5;yy<=.5;yy+=1./AA)
    {
        col += vec4(gm(coord+vec2(xx,yy)),1);
    }
    color = col/col.a;
}
