#define MAXITER 7000.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    float t = max(-2.,iTime-3.);

    float zoom = pow(1.5,-t);
    uv *= zoom;
    uv += vec2(-.732228,.217);


    vec2 c = uv;

    const vec3 yelow = vec3(240., 230., 140.)/256.;
    const vec3 pink = vec3(255., 168., 175.)/256.;
    const vec3 blue = vec3(0., 166., 147.)/256.;

    vec2 z = vec2(0);
    float iter = 0.;

    vec3 col = vec3(0);

    //col = vec3(smoothstep()) 

    for(float i = 0.; i < MAXITER; i++){
        z = vec2(z.x*z.x - z.y*z.y, 2.*z.x*z.y) + c;

        if (length(z) > 16.) break;

        iter++;
    }

    if(iter < MAXITER)col = sqrt(vec3(iter/MAXITER) * blue);



    // Output to screen
    fragColor = vec4(col,1.0);
}
