void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = mod(iTime*.05,2e4);
    float xInv = 1./iResolution.x;
    vec2 G = (floor(gl_FragCoord.xy/vec2(3.,5.))*xInv);
    vec2 M = vec2(-0.3412463,-0.2288);
    //M = (iMouse.xy-iResolution.xy*.5)*xInv;
    vec2 U = (gl_FragCoord.xy-iResolution.xy*.5);
    vec3 s = fract(sin(vec3(G.xy*95.+G.yx*35.,dot(G,vec2(37,97))))*4e5);
    s += (dot(U,M)); 
    s += time; 
    s = fract(s);

    vec3 tex = texture(iChannel0, fragCoord.xy/iResolution.xy).rgb;
    fragColor = vec4(round(tex+s-.5),1.);
}
