float ease( float x ) {                                 // rewritten from https://shadertoy.com/view/3ddBWB
	return x > .5 ? x = 1.-x, 1. - 4.* x*x*x            // stepier than smoothstep or .5-.5*cos
                  :                4.* x*x*x ;
}

#define B(x)  mod( floor(x), 2. )

void mainImage( out vec4 O, vec2 u )
{   
    O-=O; 
    vec2 R = iResolution.xy,
         U = u *= 8. / R.y;
    if (U.x > 8.) { O += .5; return; }                  // out of board

    float t = ease( fract(iTime/2.) ), T,
          y = B(U.y+1.);
    int n = int(iTime/2.+1.) % 3;
    if (n==1) { abs(U.x + 4.*t -6.) > 2. ? O++ : O; return; } // phase 2: block to left
    if (n==2) t = 1.-t, y = 1.-y, U.x = 8.-U.x;         // phase 3 = sym of phase 1

    if ( U.x < 4.*t ) { O++; return; }                  // empty area = white
    T = 2.-t;
    U.x += floor( (8.-U.x) / T ) * T - t*y;             // pack black tiles

    O += float( B(U.x) == y );                          // checker cell color          
}
