float plot_circle(vec2 pixel, float radius, float line_width)
{
    return smoothstep(0.0f, 1.0f, (abs(pixel.x * pixel.x + pixel.y * pixel.y - radius * radius) * iResolution.y / line_width));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{   
    vec2 uv = fragCoord - iResolution.xy / 2.0f; // Convert pixel coordinate into [-iResolution / 2, iResolution / 2] from [0, iResolution]
    uv /= min(iResolution.x, iResolution.y); 	 // Divide by the shorter axis to constrain coordinate in [-1, 1] along shorter axis

    float radius_1 = (sin(iTime) + 1.0f) / 4.0f;
    float radius_2 = (sin(iTime + 120.0f) + 1.0f) / 4.0f;
    float radius_3 = (sin(iTime + 240.0f) + 1.0f) / 4.0f;

    fragColor = vec4(
        mix(vec3(1.0f, 1.0f, 0.0f), vec3(0.0f, 0.0f, 0.0f), plot_circle(uv, radius_1, 1.0f)) +
        mix(vec3(0.0f, 1.0f, 1.0f), vec3(0.0f, 0.0f, 0.0f), plot_circle(uv, radius_2, 1.0f)) +
        mix(vec3(1.0f, 0.0f, 1.0f), vec3(0.0f, 0.0f, 0.0f), plot_circle(uv, radius_3, 1.0f)),
        1.0f);
}
