float smoothStep(float t)
{
    return t * t * (3.0 - 2.0 * t);
}

float lerp(float a, float b, float t)
{
    return a * (1.0 - t) + b * t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ar = iResolution.y / iResolution.x;
    vec2 uv = fragCoord/iResolution.xy;


    vec2 c00_g = vec2(1.0, 1.0);
    vec2 c10_g = vec2(-1.0, 1.0);
    vec2 c01_g = vec2(1.0, -1.0);
    vec2 c11_g = vec2(-1.0, -1.0);

    vec2 c00_d = vec2(uv.x, uv.y);
    vec2 c10_d = vec2(uv.x - 1.0, uv.y);
    vec2 c01_d = vec2(uv.x, uv.y - 1.0);
    vec2 c11_d = vec2(uv.x - 1.0, uv.y - 1.0);

    float c00 = dot(c00_g, c00_d);
    float c10 = dot(c10_g, c10_d);
    float c01 = dot(c01_g, c01_d);
    float c11 = dot(c11_g, c11_d);

    float smoothXf = smoothStep(uv.x);
    float smoothYf = smoothStep(uv.y);

    float noise = lerp(
        		  lerp(c00, c10, smoothXf),
        		  lerp(c01, c11, smoothXf),
        		  smoothYf);


    // Output to screen
    fragColor = vec4(vec3(noise),1.0);
}
