#define PI 3.1415
const float balls = 5.5;
const float speed = 6.;
const float cramming = 6.;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - iResolution.xy/2.) / iResolution.y * 2.;
	vec2 m =  (iMouse.xy - iResolution.xy/2.) / iResolution.y * 2.;
    float d = 0.;
	float r;
    float a;
    vec2 p;
    for (float i = iTime*speed+.01;  i<iTime*speed+balls; i += 1.01){
    	a = PI * 2. / balls * cramming * i;
		r = sin(iTime);
        p = vec2(sin(a)*r, cos(a)*r);
        d += 1./length(uv-p);
    }
    d /= 2.;
    vec3 col = vec3(d/40., d/35., d/25.);

    // Output to screen
    fragColor = vec4(col,1.0);
}
