// Galen Ivanov

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv *= 2.;

    vec3 col = vec3(.5+.7*sin(90.*(.40*iTime+uv.y+cos(.5*iTime+.23*uv.x)*sin(.71*iTime+uv.x+uv.y))));


    vec2 p1 = vec2(.3*sin(iTime), .3*cos(iTime));
    vec2 p2 = vec2(.7*sin(2.*iTime), -.5*cos(3.*iTime));
    vec2 p3 = vec2(.9*cos(1.53*iTime), .6*sin(2.*iTime));
    vec2 p4 = vec2(1.1*sin(1.3*iTime), .4*cos(2.1*iTime));

    float mask = length(uv-p1);
    mask *= length(uv-p2);
    mask *= length(uv-p3);
    mask *= length(uv-p4);
    mask *= 5. + 4.*sin(10.*iTime);

    col = smoothstep(.8, .81, col/mask);
    col *= 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));


    fragColor = vec4(col,1.);
}
