float myMod(float x, float y) {
    return x - y * floor(x/y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.yy;
    float zoom = 0.1 + 0.5 * (1. + sin(iTime * 0.1));
    float div = 35. * zoom;

    float f = 0.5 + 1. * (1. + sin(uv.y + iTime*0.7));
    float f2 = 0.5 + 1. * (1. + sin(uv.x + iTime*1.1));
    bool black = myMod(uv.x * div, f * 2.) < 1.;
    bool black2 = myMod(uv.y * div, f2 * 2.) < 1.;
    black = black && black2;

    // Time varying pixel color
    vec3 col = vec3(black ? 0. : 1.);


    // Output to screen
    fragColor = vec4(col,1.0);
}
