vec4 getFromColor (vec2 uv){

    vec4 color = texture(iChannel0,uv);
    return color;
}

vec4 getToColor (vec2 uv){

    vec4 color = texture(iChannel1,uv);
    return color;
}

vec4 transition (vec2 uv) {

    float pro = sin(iTime);
    // pro = 0.7;
    vec4 color = vec4(0.0,0.0,0.0,1.0);
    float offsetY = 1.0/3.0;
    if(uv.y > offsetY && uv.y < offsetY *2.0)
    {

        if(uv.x>(1.0 - pro))
        {

            color = getFromColor(vec2(uv.x - 1.0 + pro,uv.y));
        }
        else
        {
            color = getToColor(vec2(uv.x + pro,uv.y));
        }
    }
    else
    {
        if(uv.x<pro)
        {
            color = getFromColor(vec2(uv.x-pro + 1.0,uv.y));

        }
        else
        {
            color = getToColor(vec2(uv.x-pro,uv.y));
        }
    }

    return color;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = transition(uv);
}
