void mainImage( out vec4 Q, in vec2 U )
{
    Q = 2.*A(U).zwyx;
}

void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);

    vec4 m = 0.25*(A(U+vec2(0,1))+A(U+vec2(1,0))+A(U-vec2(0,1))+A(U-vec2(1,0)));
	vec4 l = (m-Q);

    vec4 O = vec4(0);
    Q += l*vec4(0,0,.5,1.);
	O.w = Q.w*.99 + 0.01*Q.z;
    O.z = Q.z+.05*Q.z*(1.-Q.z)-.03*Q.w-1e-3;
    Q = O;
    Q.zw = max(Q.zw,0.);


    if ((iFrame<1&&U.x<1.)||iMouse.z>0.&&length(U-iMouse.xy)<10.) Q=vec4(0,0,1,0);
}

