vec2 circleMid = vec2(0, 0);
float circleSize = 0.2;

vec3 col = vec3(0,0,0);

float hash1(uint n) 
{
    // integer hash copied from Hugo Elias
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return float( n & uvec3(0x7fffffffU))/float(0x7fffffff);
}

float lerp(float val1, float val2, float t){
    return (val1 + (val2 - val1)*t);
}

float lerpNoise(float value){
    float val1 = hash1(uint(value))*20. + hash1(uint(value+200.));
    float val2 = hash1(uint(value+1.))*20. + hash1(uint(value+201.));
    float decimal = value - floor(value);
    return lerp(val1, val2, decimal)/35.;
}

float getCircleRot(vec2 DistVec){
    if (DistVec.y > 0.)
    	return atan(DistVec.x / DistVec.y);
    else
        return atan(DistVec.x / DistVec.y) + 3.1415;
}

vec3 viewAnim(){
    float time = iTime;
    if (time < 1.3)
        return vec3(time*0.8, sin(iTime*10.)*(1.3-iTime), cos(iTime*10.)*(1.3-iTime));
    if (time < 1.7)
    {
        time -= 1.3;
        return vec3(0.8-time*2., 0., 0.);
    }

    return vec3((lerpNoise(time*3.+30.)/20.+(sin(iTime)+1.)/4.), (lerpNoise(time*12.)-0.5)/20., (lerpNoise(time*11.+1000.)-0.5)/20.);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    uv.y *= iResolution.y / iResolution.x;

    float beatEffect = texture(iChannel0, vec2(iTime)).x;


    vec3 animState = viewAnim();

    float zoomOut = (sin(iTime/20.)+2.) / animState.x / 10.;

    circleSize = animState.x * zoomOut;
    circleMid = animState.yz;//*beatEffect;

    vec2 circleDiff = circleMid - uv;

    float circleDist = length(circleDiff);

    //float beatEffect2 = lerp(texelFetch(iChannel0, ivec2(int(circleDist*400.), 1), 0).x, texelFetch(iChannel0, ivec2(int(circleDist*400.+1.), 1), 0).x, fract(circleDist*400.))*3.;


    float normCircleDist = circleDist / circleSize / (1.+zoomOut/10.);

	float normCircleRot = getCircleRot(circleDiff)+normCircleDist*animState.x*sin(iTime*2.);


    float circleDisplaced = normCircleDist*(sin(normCircleRot*10.-cos(iTime)*10.)+1.)/1.;

    float fadeFactor = ((clamp(circleDisplaced, 0.8*zoomOut, 1.2))-0.8*zoomOut)*5. * animState.x;

    float armFac = smoothstep(-5., 10., circleDisplaced*sin((circleDist+iTime)*20.)*(sin(iTime)+1.3)*12.0);

    col = vec3(0.5+beatEffect/2.,1,1.)*fadeFactor*armFac;
    col.g = sin(normCircleRot*10.+sin(iTime)*10.)*lerpNoise(iTime*3.+30.)*fadeFactor*armFac;

    col.b = sin(normCircleDist*beatEffect*5.)*animState.x*(1.3-armFac);// * fadeFactor;

    // Output to screen
    fragColor = vec4(col,1.0);
}
