// Click and move the mouse horizontally

const float pi = 4. * atan(1.);

void mainImage( out vec4 O, in vec2 U )
{
    vec2 p = U / iResolution.x;
    p -= vec2(0.5, 0.25);
    p *= 20.;

    vec2 mp = iMouse.xy / iResolution.x;

    float M = smoothstep (0.001, 0.,
                   (1. - mp.x) * (p.y - 3. * sin(p.x + iTime)) +
                          mp.x * (p.y - sin(3. * (p.x - iTime))));

    float grid_x = smoothstep (0., 0.2, abs(sin(pi * p.x)));
    float grid_y = smoothstep (0., 0.2, abs(sin(pi * p.y)));

    O = M * (mp.x * vec4(1.,1.,0.5,1.) +
             (1. - mp.x) * vec4(0.5,1.,1.,1.))
         * grid_x * grid_y * vec4 (1.);
}
